onClick:
{{
  (function () {
    const adminId = Dropdown_AdminUser.selectedOptionValue;
    const pwd = Input_PWD.text;

    if (!adminId) {
      showAlert("Select an admin user first", "warning");
      return;
    }

    // Step 1: verify selected user is actually an admin
    const isAdminUser = (GetUsers.data || []).some(u =>
      u.UserID === adminId &&
      (u.isAdmin === "TRUE" || u.isAdmin === true) &&
      (u.Active === "TRUE" || u.Active === true)
    );

    if (!isAdminUser) {
      showAlert("Selected user is not an admin", "error");
      return;
    }

    // Step 2: password check (shared OTP)
    if (pwd === "131021") {   // üîê change anytime
      storeValue("isAdminUnlocked", true);
      storeValue("adminUserID", adminId);

      // ‚úÖ NEW (minimal): pull latest games immediately after unlock
      GetGames.run();

      showAlert("Admin access granted", "success");
      resetWidget("Input_PWD");
    } else {
      showAlert("Invalid password", "error");
    }
  })()
}}
