onClick:
{{
  (() => {
    const gid = Dropdown_CloseGame.selectedOptionValue;

    if (!gid) {
      showAlert("Select a GameID first.", "warning");
      return;
    }

    const rowNumber = GameUtils.gameRowNumber(GetGames.data, gid);

    if (!rowNumber) {
      showAlert(
        `Row number is null. GameID=${gid}. (GetGames.data may be empty or not matching)`,
        "error"
      );
      return;
    }

    ClosePoll.run(
      { rowNumber },
      () => {
        showAlert(`Poll ${gid} closed successfully.`, "success");
        GetGames.run();
      },
      (err) => {
        showAlert(`Close failed: ${err?.message || err}`, "error");
      }
    );
  })()
}}
