onClick:
{{
  (() => {
    const gid = Dropdown_CloseGame.selectedOptionValue;

    if (!gid) {
      showAlert("Select a GameID first.", "warning");
      return;
    }

    const rowNumber = GameUtils.gameRowNumber(GetGames.data, gid);

    if (!rowNumber) {
      showAlert(`Row number not found for GameID=${gid}`, "error");
      return;
    }

    ClosePoll.run(
      { rowNumber },
      () => {
        // âœ… NEW: refresh games immediately after a successful write
        GetGames.run();

        showAlert(`Poll ${gid} closed.`, "success");
      },
      (err) => {
        showAlert(`Close failed: ${err?.message || err}`, "error");
      }
    );
  })()
}}
