onClick:
{{
  (function () {
    const gameId = Dropdown_GameFine.selectedOptionValue;
    const fine = Number(Input_FineAmount.text);

    if (!gameId) {
      showAlert("Please select a game.", "error");
      return;
    }

    if (!Number.isFinite(fine) || fine < 0) {
      showAlert("Please enter a valid fine amount.", "error");
      return;
    }

    GameFine.run(
      () => {
        showAlert("Fine amount saved.", "success");
        GetGames.run();               // refresh totals + displays
        resetWidget("Input_FineAmount");
      },
      (err) => showAlert("Error saving fine: " + err.message, "error")
    );
  })()
}}
