rowObjects.data:
{{
  (() => {
    const oldChoice = String(appsmith.store.oldChoice ?? "").trim().toUpperCase();
    const newChoice = String(appsmith.store.pendingChoice ?? "").trim().toUpperCase();

    const shouldResetRank = (oldChoice === "DROPPING" && newChoice === "IN");

    const base = {
      rowIndex: parseInt(String(appsmith.store.voteRowIndex ?? "").trim(), 10),
      GameID: String(Dropdown_Game.selectedOptionValue ?? "").trim(),
      UserID: String(appsmith.store.loggedInUserId ?? "").trim(),
      Choice: String(appsmith.store.pendingChoice ?? "").trim(),
      LastUpdated: moment().format("YYYY-MM-DD HH:mm:ss")
    };

    // Only include VoteTime/VoteOrder when you WANT re-ranking
    return shouldResetRank
      ? {
          ...base,
          VoteTime: moment().format("YYYY-MM-DD HH:mm:ss"),
          VoteOrder: String(Date.now())
        }
      : base;
  })()
}}
