text:
{{
  (() => {
    const gid = Dropdown_Game.selectedOptionValue;
    if (!gid) return "Select a game to see details.";

    const g = (GetGames.data || []).find(x => String(x.GameID) === String(gid));
    if (!g) return "Game details not found.";

    const dateTxt = g.GameDate ? moment(g.GameDate).format("ddd, DD-MMM-YYYY") : "";
    const fromTxt = g.StartTime ? String(g.StartTime) : "";
    const toTxt   = g.EndTime ? String(g.EndTime) : "";
    const venueTxt = g.Venue ? String(g.Venue) : (g.Location ? String(g.Location) : "");

    const timeTxt =
      fromTxt && toTxt ? `${fromTxt} to ${toTxt}` :
      fromTxt ? `From ${fromTxt}` :
      toTxt ? `To ${toTxt}` : "";

    const parts = [
      dateTxt ? `üìÖ ${dateTxt}` : "",
      timeTxt ? `‚è∞ ${timeTxt}` : "",
      venueTxt ? `üìç Location ${venueTxt}` : ""
    ].filter(Boolean);

    return parts.length ? parts.join("    ") : "No details available for this game.";
  })()
}}
